import React from 'react';
import { Link } from "react-router-dom";
import './app.css';  
class ViewCoupon extends React.Component {
  state = {
    response  : [],
    delete:'',
};

componentDidMount() {
  this.callApi().then(req => {
    this.setState({response:req});
  })


}
deleteRow=(props)=>{
  console.log(props.coupon_id)
}
callApi = async () => {
  const response = await fetch('/api/hello');
  const body = await response.json();
  console.log(body)
  if (response.status !== 200) throw Error(body.message);

  return body;
};
renderTableData() {
  const Shivam=this.state.response;
  return Shivam.map((student, index) => {
     const { coupon_id, coupon_name, discount } = student //destructu
     return (
        <tr key={coupon_id}>
          <td>
          <Link to={"/router"+ coupon_id}>{coupon_id}</Link>
          </td>
           <td>{coupon_name}</td>
           <td>{discount}</td>
           <button onClick={(e) => this.deleteRow(coupon_id)}>Delete Row</button>

        </tr>
     )
  })
}


render() {

  return (
   <div>
     <div id="shivu">
   <Link to='/'><button class="btn btn-primary">Add Coupon</button></Link>
   </div>
   <h1 id='title'>List Of Coupons</h1>

   <table id='students'>
      <tbody>

         {this.renderTableData()}
      </tbody>
   </table>
</div>

  )
}
}


export default ViewCoupon;
