import React, { Component } from 'react';

import { Link } from "react-router-dom";
import './app.css';  
class HomePage extends Component {
  state = {
    response: '',
    post: '',
    discount:'',
    responseToPost: '',
    loading:false
  };

  componentDidMount() {
    this.fetchData()
      .then(res => 
        this.setState({ response: "heelo" }))
      .catch(err => console.log(err));
  }

  fetchData = async () => {
    const response = await fetch('/api/hello');
    const body = await response.json();

    if (response.status !== 200) throw Error(body.message);

    return body;
  };

  handleSubmit = async e => {
    e.preventDefault();
    this.setState({loading:true});
    const response = await fetch('/api/world', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ name: this.state.post,discount:this.state.discount}),
    });
    const body = await response.text();

    this.setState({ responseToPost: body });
    this.setState({post:'',discount:'',loading:false})
    this.setState({loading:false});
  };

render() {
  const {loading}=this.state;
    return (
      <div >
        <div id="view-coupon">
        <Link to='/view'><button className="btn btn-primary">View Coupon</button></Link>
       </div>
       <div id="form">
        <form onSubmit={this.handleSubmit}>
          <p>
            <strong >Add New Coupon :</strong>
          </p>
          <input
            type="text"
            className="col-lg-12 form-control"
            placeholder="Coupon Code"
            value={this.state.post}
            onChange={e => this.setState({ post: e.target.value })}
          />
             <input
            type="text"
            className="col-sm-12 form-control"
            value={this.state.discount}
            placeholder="Discount"
            onChange={e => this.setState({ discount: e.target.value })}
          />
          <div id="message">
          <button  className="btn btn-primary "type="submit"disabled={loading}>
            {loading && <i className="fa fa-refresh fa-spin"></i>}
           {loading && <span>wait whil</span>}
           {!loading && <span>Submit</span>}
           </button>
         <p>{this.state.responseToPost}</p>
        </div>

        </form>
        </div>

      </div>
    );
  }
}
export default HomePage;
