import React from 'react';
import { Link } from "react-router-dom";
import './app.css';  
class ApplyCoupon extends React.Component {
  state = {
    response  : [],
};

componentDidMount() {
  this.callApi().then(req => {
    this.setState({response:req});
  })


}

callApi = async () => {
  const response = await fetch('/api/hello');
  const body = await response.json();
  console.log(body)
  if (response.status !== 200) throw Error(body.message);

  return body;
};
renderTableData() {
  const Shivam=this.state.response;
  return Shivam.map((student, index) => {
     const { coupon_id, coupon_name, discount } = student //destructuring
     return (
        <tr key={coupon_id}>
           <td>{coupon_id}</td>
           <td>{coupon_name}</td>
           <td>{discount}</td>
            <td><button class="btn btn-primary">ApplyCoupon</button></td>
        </tr>
     )
  })
}


render() {

  return (
   <div>
     <div id="shivu">
   <Link to='/'><button class="btn btn-primary">Add Coupon</button></Link>
   </div>
   <h1 id='title'>List Of Coupons</h1>

   <table id='students'>
      <tbody>

         {this.renderTableData()}
      </tbody>
   </table>
</div>

  )
}
}


export default ApplyCoupon;
