                        <input type="file" name="file" onChange={this.onChangeHandler}/>
                        <button type="button" class="btn btn-success btn-block" onClick={this.onClickHandler}>Upload</button> 
                        </div>
                </div>
            </div>
        </div>
    )
  }
  } 

