import React, { Component } from "react";

import Autocomplete from "react-autocomplete";

import { debounce } from "lodash";
import "./app.css";
import axios from "axios";

class HomePage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      value: "",
     response:[]

    };
  }

  getData = () => {
    var items = this.state.response;
    // console.log(items)
    return items.map((item) => {
      return <option value={item.label} onSelect={this.handleSelect}
    >{item.label}</option>;
    });
  };
  callAPI = debounce(() => {
    const { value } = this.state;
    console.log("API Called");
    axios
      .get(`/api/hello?name=${value}`)
      .then((finalRes) => {
        console.log(finalRes.data);
        this.setState({response:finalRes.data})
        //api response will put inside the aaray 
      })
      .catch((err) => {
        console.log(err);
      });
  }, 500);

  handleChange = (event) => {
    this.setState({ value: event.target.value });
    console.log(this.state.value);
    this.callAPI();
  };
  handleSelect = (event) => {
    alert(this.state.value)
  };

  render() {
    console.log(this.state.value)
    return (

      <div style={{marginTop:'100px'}} >


<div class="card col-sm-4 offset-sm-4" style={{backgroundColor:"#F2F3F4"}}>
  <div class="card-body">
    <h5 class="card-title text-center" style={{fontSize:'30px',fontWeight:'bold'}}>Uplaod a File</h5>
      <div>
      <form className="offset-sm-3 col-sm-6" style={{marginTop:"50px"}}>
  <div class="form-group">

    <input  type="file" class="form-control-file" />
    <button type="button" class="btn btn-primary offset-sm-3" style={{marginTop:'40px'}}>Submit</button>
  </div>
</form>
      </div>
  </div>
</div>
   </div>
    );
  }
}
export default HomePage;
