import React from 'react';
import { List } from "semantic-ui-react";

class ViewCoupon extends React.Component {
  state = {
    response  : [],
};

componentDidMount() {
  this.callApi().then(req => {
    this.setState({response:req});
  })


}

callApi = async () => {
  const response = await fetch('/api/hello');
  const body = await response.json();

  if (response.status !== 200) throw Error(body.message);

  return body;
};

render() {
  const Shivam=this.state.response;
  return (
     <div><List>
     {Shivam.map(el => {
       return (
         <List.Item  key={el.coupon_id}>
           <List.Content>
           {el.id}     {el.coupon_name} {el.discount}
           </List.Content>

         </List.Item>
       );
     })}
   </List></div>
  )
}
}
export default ViewCoupon;
