import React, { Component } from 'react';
import $ from "jquery";
import Autocomplete from 'react-autocomplete';
import { Link } from "react-router-dom";
import { debounce } from 'lodash';
import './app.css';  


  class HomePage extends Component {
    constructor(props) {
        super(props);
        this.state = {
          response: '',
          post: '',
          discount:'',
          responseToPost: '',
          arr:[],
        }
       this.handleInputThrottled = debounce(this.handleInput, 100)
    }




  fetchData = async (e) => {


    const response = await fetch('/api/hello');
    const body = await response.json();

    if (response.status !== 200) throw Error(body.message);

    return body;
  };

  handleSubmit =async (e) => {
    this.setState({ discount: e.target.value })

    var shiv=this.state.discount;
    console.log(shiv)
    const response = await fetch(`/api/hello?name=${shiv}`, {
      method: 'GET',
      dataType:"jsonp",
    });
    const body = await response.json();
    console.log(body)
   this.setState({arr:body})


  };

render() {

    return (
      <div >

      <input type="text" name="filtrname" id="searchName" placeholder="Enter a name" 
      value={this.state.discount} 
      ref="autoCompleteInput"  
      onChange={this.handleSubmit}/>
        <Autocomplete
        getItemValue={(item) => item.label}
  items={this.state.arr}

  renderItem={(item, isHighlighted) =>
    <div style={{ background: isHighlighted ? 'lightgray' : 'white' }}>
      {item.label}

    </div>
  }

        />
      </div>
    );
  }
}
export default HomePage;
