import React, { Component } from 'react';

import { Link } from "react-router-dom";
import './app.css';  
class HomePage extends Component {
  state = {
    response: '',
    post: '',
    discount:'',
    responseToPost: '',

  };

  componentDidMount() {
    this.fetchData()
      .then(res => 
        this.setState({ response: "heelo" }))
      .catch(err => console.log(err));
  }

  fetchData = async () => {
    const response = await fetch('/api/hello');
    const body = await response.json();

    if (response.status !== 200) throw Error(body.message);

    return body;
  };

  handleSubmit = async e => {
    e.preventDefault();
    this.setState({loading:true});
    const response = await fetch('/api/world', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ name: this.state.post,discount:this.state.discount}),
    });
    const body = await response.text();

    this.setState({ responseToPost: body });
    this.setState({post:'',discount:'',loading:false})


  };

render() {

    return (
      <div >

      <input type="text" name="filtrname" id="searchName" placeholder="Enter a name"/>

      </div>
    );
  }
}
export default HomePage;
