import React from 'react';
import { List } from "semantic-ui-react";
import './app.css';  
class ViewCoupon extends React.Component {
  state = {
    response  : [],
};

componentDidMount() {
  this.callApi().then(req => {
    this.setState({response:req});
  })


}

callApi = async () => {
  const response = await fetch('/api/hello');
  const body = await response.json();

  if (response.status !== 200) throw Error(body.message);

  return body;
};
renderTableData() {
  const Shivam=this.state.response;
  return Shivam.map((student, index) => {
     const { id, coupon_name, discount } = student //destructuring
     return (
        <tr key={id}>
           <td>{id}</td>
           <td>{coupon_name}</td>
           <td>{discount}</td>

        </tr>
     )
  })
}

render() {

  return (
    /*
     <div><List>
     {Shivam.map(el => {
       return (
         <List.Item  key={el.id}>
           <List.Content>
           {el.id}     {el.coupon_name} {el.discount}
           </List.Content>

         </List.Item>
       );
     })}
   </List></div>*/
   <div>
   <h1 id='title'>List Of Coupons</h1>
   <table id='students'>
      <tbody>
         {this.renderTableData()}
      </tbody>
   </table>
</div>

  )
}
}


export default ViewCoupon;
