import React from 'react';
import { Link } from "react-router-dom";
import './app.css';  
class ViewCoupon extends React.Component {
  state = {
    response  : [],
    delete:'',
};

componentDidMount() {
  this.callApi().then(req => {
    this.setState({response:req});
  })


}
deleteRow=async(id)=>{
  console.log(id)
  const response = await fetch('/api/delete', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({ post: id}),
  });
}
callApi = async () => {
  const response = await fetch('/api/hello');
  const body = await response.json();
  console.log(body)
  if (response.status !== 200) throw Error(body.message);

  return body;
};
renderTableData() {
  const Shivam=this.state.response;
  return Shivam.map((student, index) => {
     const { coupon_id, coupon_name, discount } = student //destructu
     return (
        <tr key={coupon_id}>
          <td>

          </td>
           <td>{coupon_name}</td>
           <td>{discount}</td>
          <td><button onClick={(e) => this.deleteRow(coupon_id)}>Delete Row</button></td>

        </tr>
     )
  })
}


render() {

  return (
   <div>
     <div id="shivu">
   <Link to='/'><button className="btn btn-primary">Add Coupon</button></Link>
   </div>
   <h1 id='title'>List Of Coupons</h1>

   <table id='students'>
      <tbody>

         {this.renderTableData()}
      </tbody>
   </table>
</div>

  )
}
}


export default ViewCoupon;
