import React, { Component } from "react";

import Autocomplete from "react-autocomplete";

import { debounce } from "lodash";
import "./app.css";
import axios from "axios";

class HomePage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      value: "",
      selectedFile: null

    };
  }
handleSubmit =(event)=>{
  alert("hello")

  const formData = new FormData(); 

  // Update the formData object 
  formData.append( 
    "myFile", 
    this.state.selectedFile, 
    this.state.selectedFile.name 
  ); 

  axios
  .post(
    '/api/hello',
    formData
  )
  .then((resp) => {
   console.log(resp)
    // let Rating = [
    //   { title: "Grntee ", value: res["Grntee"] },
    //   { title: "Rating ", value: res["Rating"] },
    //   { title: "Rating_Agency ", value: res["Rating_Agency"] }
    // ];
  })
  .catch((error) => {
    // this.setState({ error, isLoading: false })
    console.log(error);
  });
  event.preventDefault();

}

  handleChange = (event) => {
    this.setState({ selectedFile: event.target.files[0] }); 
  };


  render() {
    //console.log(this.state.value)
    return (

      <div style={{marginTop:'100px'}} >


<div class="card col-sm-4 offset-sm-4" style={{backgroundColor:"#F2F3F4"}}>
  <div class="card-body">
    <h5 class="card-title text-center" style={{fontSize:'30px',fontWeight:'bold'}}>Uplaod a File</h5>
      <div>
      <form className="offset-sm-3 col-sm-6" style={{marginTop:"50px"}} enctype="multipart/form-data">
  <div class="form-group">

    <input  type="file" 
    value={this.state.selectedFile}
    onChange={this.handleChange}
    class="form-control-file" />
    <button type="button" class="btn btn-primary offset-sm-3"onClick={this.handleSubmit} style={{marginTop:'40px'}}>Submit</button>
  </div>
</form>
      </div>
  </div>
</div>
   </div>
    );
  }
}
export default HomePage;
